# https://leetcode.com/problems/palindromic-substrings/

class Solution:
    def countSubstrings(self, s: str) -> int:
        count = 0
        for i in range(len(s)):
            substring_forward = ""
            substring_backward = ""
            for j in range(i, len(s)):
                substring_forward += s[j]
                substring_backward = s[j] + substring_backward
                if substring_forward == substring_backward:
                    count += 1
        return count


s = Solution()
print(s.countSubstrings(""))
